def tbats_forecast(mod, h):  # this function is the same as bats_forecast
    fcst = np.zeros(h)
    xx = np.zeros((h, mod["x"].shape[1]))
    w = mod["w_transpose"][0]

    fcst[0] = np.dot(w, mod["x"][-1])
    xx[0] = np.dot(mod["F"], mod["x"][-1])

    if h > 1:
        for t in range(1, h):
            xx[t] = np.dot(mod["F"], xx[t - 1])
            fcst[t] = np.dot(w, xx[t - 1])

    res = {"mean": fcst}

    return res

